@Inject
private Instance<MailMessage> mailMessage;

public void sendMail() {

	MailMessage m = mailMessage.get();
	m.from("John Doe<john@test.com>")
       .to("Jane Doe<jane@test.com>")
       .subject(subject)
       .bodyHtml(htmlBody)
